const r=document.getElementById("start-recording"),c=document.getElementById("stop-recording"),d=document.getElementById("recording-timer");let n=null;function i(t){let e=0;return n=setInterval(()=>{e++,t(e)},1e3),n}function s(t){clearInterval(t)}function l(t){const e=String(Math.floor(t/60)).padStart(2,"0"),o=String(t%60).padStart(2,"0");d.textContent=`${e}:${o}`}r.addEventListener("click",()=>{console.log("Recording started"),i(t=>{console.log("Left:",t),l(t)})});c.addEventListener("click",()=>{console.log("Recording stopped"),s(n)});
